import React, {useState, useEffect} from 'react';//importa o reac para o arquivo
import { Link } from 'react-router-dom';//importa o uso de links sem precisar recarregar a pagina
import { FiPower, FiTrash2 } from 'react-icons/fi';//importa icones

import api from '../../services/api';//importa a api

import logoImg from '../../assets/logo.svg';//importa logo da pagina de login

import './style.css';//importa o css


export default function profile(){
    const [incidents, setIncidents] = useState();

    const ongId = localStorage.getItem('ongId');//recebe o idd da ing salva no cabeçalho
    const ongName = localStorage.getItem('ongName');//recebe pelo cabeçalho http o nome da ong

    useEffect(() => {
        api.get('profile', {
            headers:{
                Authorization: ongId,
            }
        }).then(response => {
           setIncidents(response.data);
        })
    }, [ongId]);

    return(
        <div class="profile-container">
            <header>
                <img src={logoImg} alt="Be The Hero" />
                <span>Bem vinda, {ongName}</span>

                <Link className="button" to="/incidents/new">Cadastrar novo caso</Link>
                <button type="button">
                    <FiPower size={18} color="#E03041" />
                </button>
            </header>

            <h1>Casos cadastrados</h1>

            <ul>
                <li>
                    <strong>CASO:</strong>
                    <p>Caso teste</p>

                    <strong>DESCRIÂO:</strong>
                    <p>Descrição teste</p>

                    <strong>VALOR:</strong>
                    <p>R$ 120,00</p>

                    <button type="button">
                       <FiTrash2 size={20} color="#a8a8b3" />
                    </button>
                </li>
            </ul>
        </div>
    );
}
