import React, {useState} from 'react';
import { Link } from 'react-router-dom';//importa o uso de links sem precisar recarregar a pagina
import { FiLogIn } from 'react-icons/fi';//importa os incones

import api from '../../services/api';//importa a api


import './styles.css';//importa css
import heroesImg from '../../assets/heroes.png';//importa img
import logoImg from '../../assets/logo.svg';//importa logo da pagina de login


export default function Logon(){
    const [id,setId] = useState('');

    async function handleLogin(e){
        e.preventDefault();

        try{
          const response = await api.post('sessions', {id})

          console.log(response.data.name)
        }catch(err){

        }
    }

    return (
        <div class="logon-container">
            <section className="form">

            <img src= {logoImg} alt="Be The Hero"/>

             <form onSubmit={handleLogin}>
                 <h1>Faça seu logon</h1>

                 <input
                  placeholder="Sua ID" 
                 value={id}
                 onChange={e=> setId(e.target.value)}
                 />


                 <button type="submit"  className="button">Entrar</button>

                 <Link className="back-link" to="/register">
                     <FiLogIn size={16} color="E02041"/>
                     Não tenho cadastro
                 </Link>

             </form>
            </section>

            <img src= {heroesImg} alt="Heroes"/>
        </div>
    );
}
